function submitComment() {
    const name = document.getElementById('name').value;
    const comment = document.getElementById('comment').value;
    
    // Aquí se puede agregar código para enviar el comentario a la base de datos
    // Ejemplo: enviarComentario(name, comment);
    
    const table = document.getElementById('commentTable');
    const row = table.insertRow();
    row.insertCell(0).innerText = name;
    row.insertCell(1).innerText = comment;

    // Limpiar campos
    document.getElementById('name').value = '';
    document.getElementById('comment').value = '';
}
function showComments() {
    const content = document.getElementById('content');
    content.innerHTML = `
        <h2>Comentarios</h2>
        <table>
            <tr>
                <th>Nombre</th>
                <th>Comentario</th>
            </tr>
            <tbody id="commentTable"></tbody>
        </table>
        <input type="text" id="name" placeholder="Tu nombre">
        <textarea id="comment" placeholder="Tu comentario"></textarea>
        <button onclick="submitComment()">Enviar</button>
        <button onclick="goBack()">Regresar</button>
    `;
    content.style.display = 'block';
}

function submitComment() {
    const name = document.getElementById('name').value;
    const comment = document.getElementById('comment').value;
    
    // Aquí se puede agregar código para enviar el comentario a la base de datos
    // Ejemplo: enviarComentario(name, comment);
    
    const table = document.getElementById('commentTable');
    const row = table.insertRow();
    row.insertCell(0).innerText = name;
    row.insertCell(1).innerText = comment;

    // Limpiar campos
    document.getElementById('name').value = '';
    document.getElementById('comment').value = '';
}
